mapper.select:
select

from book_log

    
        and id = #{id, jdbcType=INTEGER}
    
    
        id in (
            
                #{item}
            
        )
    
    
        and user_id = #{userId}
    
    
        and book_id = #{bookId}
    
    
        and state = #{state}
    

order by update_time desc

select
count(*)
from book_log

    
        and id = #{id, jdbcType=INTEGER}
    
    
        id in (
        
            #{item}
        
        )
    
    
        and user_id = #{userId}
    
    
        and book_id = #{bookId}
    
    
        and state = #{state}
