mapper.select:
select

from user
where id = #{id, jdbcType=INTEGER}

select

from user
where job_number = #{jobNumber, jdbcType=CHAR}

select

from user
where openid = #{openid, jdbcType=CHAR}

select

from user

    
        and id = #{id, jdbcType=INTEGER}
    
    
        and id in (
            
                #{item}
            
        )
    
    
        and job_number like "%"#{jobNumber, jdbcType=CHAR}"%"
    
    
        and nick_name like "%"#{nickName, jdbcType=VARCHAR}"%"
    
    
        and full_name like "%"#{fullName, jdbcType=VARCHAR}"%"
    
    
        and gender = #{gender, jdbcType=CHAR}
    
    
        and phone like "%"#{phone, jdbcType=CHAR}"%"
    
    
        and email like "%"#{email, jdbcType=VARCHAR}"%"
    
    
        and department = #{department, jdbcType=CHAR}
    
    
        and available = #{available, jdbcType=BOOLEAN}
    


    
        order by point desc
    
    
        order by update_time desc
